if(typeof HS_STARTUP_FOLDED==='undefined'){var HS_STARTUP_FOLDED=false}if(typeof HS_SHOW_ALL_TEXT==='undefined'){var HS_SHOW_ALL_TEXT='[Expand all]'}if(typeof HS_HIDE_ALL_TEXT==='undefined'){var HS_HIDE_ALL_TEXT='[Collapse all]'}if(typeof HS_ALWAYS_DISPLAY_ICON==='undefined'){var HS_ALWAYS_DISPLAY_ICON=false}if(typeof HS_ICON_CLOSED==='undefined'){var HS_ICON_CLOSED='&#x25BA;'}if(typeof HS_ICON_OPEN==='undefined'){var HS_ICON_OPEN='&#x25BC;'}if(typeof HS_ICON_EMPTY==='undefined'){var HS_ICON_EMPTY='&#x25A0;'}if(typeof HS_SHOW_ALL_OPEN_DONE_TREES==='undefined'){var HS_SHOW_ALL_OPEN_DONE_TREES=false}if(typeof HS_CLASS==='undefined'){var HS_CLASS='hsCollapsible'}function hsExpand(header){hsExpand2(header,true)}function hsExpand2(header,expandDoneHeader){if(!header.parent().hasClass(HS_CLASS))return;if(hsIsDoneHeader(header)&&!expandDoneHeader)return;header.children('span[class="ellipsis"]').remove();if(HS_ALWAYS_DISPLAY_ICON==true){header.append('<span class="ellipsis"> '+HS_ICON_OPEN+'</span>')}header.parent().removeClass('hsCollapsed').addClass('hsExpanded');header.nextAll().show()}function hsExpandParents(header){hsExpand(header);header.parents('.hsCollapsed').each(function(){hsExpand($(this).children(':header'))})}function hsCollapseParents(header){hsCollapse(header);header.parents('.hsExpanded').each(function(){hsCollapse($(this).children(':header'))})}function hsCollapse(header){if(!header.parent().hasClass(HS_CLASS))return;header.children('span[class="ellipsis"]').remove();header.append('<span class="ellipsis"> '+HS_ICON_CLOSED+'</span>');header.parent().removeClass('hsExpanded').addClass('hsCollapsed');header.nextAll().hide()}function hsToggleCollapsing(header){if(header.parent().hasClass('hsCollapsed'))hsExpand(header);else if(header.parent().hasClass('hsExpanded'))hsCollapse(header)}function hsExpandAll(){$('#content .hsCollapsed').each(function(){hsExpand2($(this).children(':header'),HS_SHOW_ALL_OPEN_DONE_TREES)})}function hsCollapseAll(){$('#content .hsExpanded').each(function(){hsCollapse($(this).children(':header'))})}function hsCollapseAllVisible(){$('#content .hsExpanded:visible').each(function(){hsCollapse($(this).children(':header'))})}function hsInit(){for(var i=3;i<=5;i++){$('#content .outline-'+i).each(function(){var header=$(this).children(':header');if(header.siblings().length>0){$(this).addClass(HS_CLASS);header.css({cursor:'pointer'});header.click(function(){hsToggleCollapsing($(this))});if(header.parent().hasClass('hsCollapsed')){hsCollapse(header)}else if(header.parent().hasClass('hsExpanded')){hsExpand(header)}else{hsSetDefaultVisibility(header)}}else{if(HS_ALWAYS_DISPLAY_ICON==true){header.append('<span class="ellipsis"> '+HS_ICON_EMPTY+'</span>')}$(this).addClass('hsEmpty')}})}$('.title').after($('<div class="buttons dontprint"></div>'));$('.buttons').append($('<span>'+HS_SHOW_ALL_TEXT+'</span>').addClass('hsButton').click(hsExpandAll));$('.buttons').append($('<span>'+HS_HIDE_ALL_TEXT+'</span>').addClass('hsButton').click(hsCollapseAll))}function hsIsDoneHeader(header){return $('span.done',header).length}function hsSetDefaultVisibility(header){if(HS_STARTUP_FOLDED){hsCollapse(header)}else{if(!hsIsDoneHeader(header)|HS_SHOW_ALL_OPEN_DONE_TREES){hsExpand(header)}else{hsCollapse(header)}}}function hsExpandAnchor(id){if(id){$(id).parents('.hsCollapsed').each(function(){hsExpand2($(this).children(':header'),true)})}}function hsReviewTaskNext(backward){if($('.outline-2 span.todo').length==0)return;if($('.hsReviewing').length==0){$('body').addClass('hsReviewing');$('.hsReviewPanel').addClass('hsReviewing');$('body').prepend('<div id="hsOverlay"></div>');hsAddReviewingPanels();hsCollapseAll();$('.outline-2').each(function(){var firstTodo=$(this).find('span.todo:first').parent().parent();firstTodo.addClass('hsReview');hsExpandParents($(firstTodo).children(':header'))})}else{var todosElements=$('.outline-2:visible span.todo').parent().parent();var todos=jQuery.makeArray(todosElements);if(backward){todos=todos.reverse()}var foundReview=false;var index;for(index=0;index<todos.length;++index){var todo=todos[index];if(foundReview){$(todo).addClass('hsReview');hsExpandParents($(todo).children(':header'));break}if($(todo).hasClass('hsReview')){foundReview=true;if(index<todos.length-1){$(todo).removeClass('hsReview');hsCollapseParents($(todo).children(':header'))}}}if(index<todos.length){var reviewItem=index+1;if(backward){reviewItem=todos.length-index}$('.outline-2:visible .hsReviewingPanel .hsReviewItem').text(reviewItem)}}$('html, body').animate({scrollTop:$(".hsReview:visible").offset().top},200)}function hsReviewTaskPrev(){hsReviewTaskNext(true)}function hsReviewTaskQuit(){$('body').removeClass('hsReviewing');$('.hsReviewPanel').removeClass('hsReviewing');$('.hsReview').removeClass('hsReview');$('#hsOverlay').remove();$('.hsReviewingPanel').remove()}function hsHideTodoKeyword(kw){$('span.'+kw).addClass('hsHidden').parent().parent().hide()}function hsShowTodoKeyword(kw){$('span.'+kw).removeClass('hsHidden').parent().parent().show()}function hsAddReviewPanels(){$('.outline-2').each(function(e){var nbItems=$(this).find('span.todo').length;if(nbItems>0){$(this).prepend('<div class="hsReviewPanel hsUnselectable" onclick="hsReviewTaskNext()">'+'Press r or click here<br>'+'to review '+nbItems+' tasks<br>'+'(out of '+nbReviewTotalTasks+' tasks)</div>')}})}function hsAddReviewingPanels(){$('.outline-2').each(function(e){var nbItems=$(this).find('span.todo').length;if(nbItems>0){$(this).prepend('<div class="hsReviewingPanel hsUnselectable">Reviewing task <span class="hsReviewItem">1</span> / '+nbItems+'<br>'+'(out of '+nbReviewTotalTasks+' tasks)<br>'+'Shortcuts: '+'<span class="hsReviewButton" onclick="hsReviewTaskNext()">r (next)</span> - '+'<span class="hsReviewButton" onclick="hsReviewTaskPrev()">R (previous)</span>'+'</div>')}else{$(this).prepend('<div class="hsReviewingPanel hsUnselectable">No task to review<br>'+'(out of '+nbReviewTotalTasks+' tasks)<br>'+'Shortcuts: '+'<span class="hsReviewButton" onclick="hsReviewTaskNext()">r (next)</span> - '+'<span class="hsReviewButton" onclick="hsReviewTaskPrev()">R (previous)</span>'+'</div>')}})}var nbReviewTotalTasks;$(document).ready(function(){nbReviewTotalTasks=$('.outline-2 span.todo').length;hsAddReviewPanels()});
